import { Component } from '@angular/core';
import { SellerService } from '../services/seller.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-seller-auth',
  templateUrl: './seller-auth.component.html',
  styleUrls: ['./seller-auth.component.css']
})
export class SellerAuthComponent {
  constructor(private seller: SellerService,private router:Router) { }
  showlogin=false;
  ngOnInit()
  {
    this.seller.reloadSeller();
  }
  signup(val: any) {
  this.seller.usersignup(val)


  }
  openlogin(){
    this.showlogin=true;

  }
  Signup(){
    this.showlogin=false;
  }
}

<div class="sell">
    <div *ngIf="!showlogin" class="sup">
        <h1>Seller Sign Up</h1>
        <form #sf="ngForm" (ngSubmit)="signup(sf.value)">
            <input type="text" placeholder="Enter Name" ngModel name="name">
            <input type="text" placeholder="Password" ngModel name="password">
            <input type="text" placeholder="Email" ngModel name="email">
            <button>Sign Up</button><br>
            <a (click)="openlogin()">Already Have Account? Click to Login</a>

        </form> 
</div>
<div *ngIf="showlogin" class="sell">
    <div class="sup">
        <h1>Seller Login</h1>
        <form #sf="ngForm" (ngSubmit)="signup(sf.value)">
            <input type="text" placeholder="Enter Name" ngModel name="name">
            <input type="text" placeholder="Password" ngModel name="password">
            <input type="text" placeholder="Email" ngModel name="email">
            <button>Login</button>
            <a class="siup" (click)="Signup()">Click to SignUp</a>
          </form> 
</div>
</div>
